const buttonHamburger = document.querySelector(".header__hamburger");
const headerNav = document.querySelector(".header__navigation");

buttonHamburger.addEventListener("click", () => {
  const currentState = buttonHamburger.getAttribute("data-state");

  if (!currentState || currentState === "closed") {
    buttonHamburger.setAttribute("data-state", "opened");
    buttonHamburger.setAttribute("aria-expanded", true);
    headerNav.setAttribute("data-visible", true);
  } else {
    buttonHamburger.setAttribute("data-state", "closed");
    buttonHamburger.setAttribute("aria-expanded", false);
    headerNav.setAttribute("data-visible", false);
  }
});

const observer = new IntersectionObserver((entries) => {
  entries.forEach((entry) => {
    if (entry.isIntersecting) {
      entry.target.classList.add("show");
    }

    // else {
    //   entry.target.classList.remove("show");
    // }
  });
});

const hiddenElements = document.querySelectorAll(".hidden");
hiddenElements.forEach((el) => observer.observe(el));

// Progress Bar
window.onscroll = function () {
  myFunction();
};

function myFunction() {
  let winScroll = document.body.scrollTop || document.documentElement.scrollTop;
  let height =
    document.documentElement.scrollHeight -
    document.documentElement.clientHeight;
  let scrolled = (winScroll / height) * 100;
  document.getElementById("myBar").style.width = scrolled + "%";
}

// Scrolling
let prevScrollpos = window.pageYOffset;
window.onscroll = function () {
  let currentScrollPos = window.pageYOffset;
  if (prevScrollpos > currentScrollPos) {
    document.getElementById("navbar").style.top = "0";
  } else {
    let clientHeight = document.getElementById("navbar").offsetHeight;
    // let clientHeight = document.getElementById("navbar").clientHeight;
    document.getElementById("navbar").style.top = "-" + clientHeight + "px";
  }
  prevScrollpos = currentScrollPos;
};

// Read more
function readmore() {
  let dots = document.getElementById("dots");
  let moreText = document.getElementById("more");
  let btnText = document.getElementById("myBtn");

  if (dots.style.display === "none") {
    dots.style.display = "inline";
    btnText.innerHTML = "Read more";
    moreText.style.display = "none";
  } else {
    dots.style.display = "none";
    btnText.innerHTML = "Read less";
    moreText.style.display = "inline";
  }
}

